package com.spreadlov.app.util

import android.content.ActivityNotFoundException
import android.content.Context
import android.content.Intent
import android.net.Uri
import android.widget.Toast

object IntentUtils {

    private const val PLAY_STORE_PACKAGE = "com.android.vending"
    private const val APP_PACKAGE_NAME = "com.spreadlov.app"

    fun shareApp(context: Context) {
        val shareText = "Check out SpreadLov â€“ a free, genuine dating platform! ğŸ’•\nhttps://spreadlov.com"
        val shareIntent = Intent(Intent.ACTION_SEND).apply {
            type = "text/plain"
            putExtra(Intent.EXTRA_TEXT, shareText)
            putExtra(Intent.EXTRA_SUBJECT, "SpreadLov - Dating App")
        }
        context.startActivity(Intent.createChooser(shareIntent, "Share SpreadLov via"))
    }

    fun rateApp(context: Context) {
        try {
            val playStoreIntent = Intent(Intent.ACTION_VIEW).apply {
                data = Uri.parse("market://details?id=$APP_PACKAGE_NAME")
                setPackage(PLAY_STORE_PACKAGE)
            }

            if (isPlayStoreInstalled(context)) {
                context.startActivity(playStoreIntent)
            } else {
                openPlayStoreInBrowser(context)
            }
        } catch (e: ActivityNotFoundException) {
            openPlayStoreInBrowser(context)
        } catch (e: Exception) {
            Toast.makeText(context, "Unable to open Play Store", Toast.LENGTH_SHORT).show()
        }
    }

    private fun isPlayStoreInstalled(context: Context): Boolean {
        return try {
            context.packageManager.getPackageInfo(PLAY_STORE_PACKAGE, 0)
            true
        } catch (e: Exception) {
            false
        }
    }

    private fun openPlayStoreInBrowser(context: Context) {
        val webIntent = Intent(
            Intent.ACTION_VIEW,
            Uri.parse("https://play.google.com/store/apps/details?id=$APP_PACKAGE_NAME")
        )
        context.startActivity(webIntent)
    }
}

package com.spreadlov.app.util

import android.content.Context
import android.content.Intent
import android.graphics.Bitmap
import android.net.Uri
import android.webkit.*
import androidx.webkit.WebViewFeature
import androidx.webkit.WebSettingsCompat
import androidx.webkit.WebViewFeature.SAFE_BROWSING_ENABLE

object WebViewClientHelper {

    private const val ALLOWED_DOMAIN = "spreadlov.com"

    fun setupWebView(
        webView: WebView,
        context: Context,
        onPageStarted: () -> Unit,
        onPageFinished: () -> Unit,
        onError: () -> Unit
    ) {
        webView.settings.apply {
            javaScriptEnabled = true
            domStorageEnabled = true
            databaseEnabled = true
            cacheMode = WebSettings.LOAD_DEFAULT
            mixedContentMode = WebSettings.MIXED_CONTENT_NEVER_ALLOW
            allowFileAccess = false
            allowContentAccess = true
            setSupportMultipleWindows(false)
            javaScriptCanOpenWindowsAutomatically = false
            mediaPlaybackRequiresUserGesture = false
            setSupportZoom(true)
            builtInZoomControls = true
            displayZoomControls = false
        }

        if (WebViewFeature.isFeatureSupported(SAFE_BROWSING_ENABLE)) {
            WebSettingsCompat.setSafeBrowsingEnabled(webView.settings, true)
        }

        CookieManager.getInstance().apply {
            setAcceptCookie(true)
            setAcceptThirdPartyCookies(webView, true)
        }

        webView.webViewClient = object : WebViewClient() {
            override fun onPageStarted(view: WebView?, url: String?, favicon: Bitmap?) {
                super.onPageStarted(view, url, favicon)
                onPageStarted()
            }

            override fun onPageFinished(view: WebView?, url: String?) {
                super.onPageFinished(view, url)
                onPageFinished()
            }

            override fun shouldOverrideUrlLoading(view: WebView?, request: WebResourceRequest?): Boolean {
                val url = request?.url.toString()

                return when {
                    url.contains(ALLOWED_DOMAIN) -> false
                    url.startsWith("mailto:") || url.startsWith("tel:") || 
                    url.startsWith("whatsapp:") || url.startsWith("sms:") -> {
                        handleExternalIntent(context, url)
                        true
                    }
                    else -> {
                        val intent = Intent(Intent.ACTION_VIEW, Uri.parse(url))
                        context.startActivity(intent)
                        true
                    }
                }
            }

            override fun onReceivedError(
                view: WebView?,
                request: WebResourceRequest?,
                error: WebResourceError?
            ) {
                super.onReceivedError(view, request, error)
                if (request?.isForMainFrame == true) {
                    onError()
                }
            }
        }

        webView.webChromeClient = object : WebChromeClient() {
            override fun onProgressChanged(view: WebView?, newProgress: Int) {
                super.onProgressChanged(view, newProgress)
            }

            override fun onShowFileChooser(
                webView: WebView?,
                filePathCallback: ValueCallback<Array<Uri>>?,
                fileChooserParams: FileChooserParams?
            ): Boolean {
                return true
            }
        }
    }

    private fun handleExternalIntent(context: Context, url: String) {
        try {
            val intent = Intent(Intent.ACTION_VIEW, Uri.parse(url))
            context.startActivity(intent)
        } catch (e: Exception) {
            e.printStackTrace()
        }
    }
}

